changeSet.sql:
create or replace FUNCTION get_accno
               (
                   json_val clob
               ) 
               RETURN Varchar2
               IS
                   V_DATE_TXN   VARCHAR2(50);
               BEGIN
               select name into V_DATE_TXN
                   from json_table(
                       json_val,
                       '$.bankAccountCollection[*]'
                       columns
                       (
                           name varchar2(100 char) path '$.bacAccountNumber',
                           value varchar2(100 char) path '$.bacOverallDefault'
                       )
                   )
                   where value = 1;

                   RETURN V_DATE_TXN;
               END;
