-- Query Part
i_BODY_QUERY := TO_CLOB('SELECT
      "BRANCH CODE",
      "BRANCH NAME",
      "TERMINAL",
      "LOCATION",
      "ATM COUNT",
      SUM("ATM TOTAL") "ATM TOTAL",
      "CAM COUNT",
      SUM("CAM TOTAL")"CAM TOTAL",
      "BRM COUNT",
       SUM("BRM TOTAL")"BRM TOTAL",
      SUM("ATM COUNT" + "CAM COUNT"+"BRM COUNT") "TOTAL COUNT",
      SUM((10.00 * "ATM COUNT") + (10.00 * "CAM COUNT")+(10.00* "BRM COUNT")) "TOTAL"
FROM(
SELECT
      "BRANCH CODE",
      "BRANCH NAME",
      "TERMINAL",
      "LOCATION",
      COUNT("ATM COUNT") "ATM COUNT",
      SUM("ATM TOTAL")"ATM TOTAL",
      COUNT("CAM COUNT") "CAM COUNT",
      SUM("CAM COUNT") "CAM TOTAL",
      COUNT("BRM COUNT") "BRM COUNT",
      SUM("BRM COUNT") "BRM TOTAL",
      SUM("ATM COUNT" + "CAM COUNT" + "BRM COUNT") "TOTAL COUNT",
      SUM((10.00 * "ATM COUNT") + (10.00 * "CAM COUNT")+(10.00 * "BRM COUNT")) "TOTAL"
FROM (
SELECT
      ABR.ABR_CODE "BRANCH CODE",
      ABR.ABR_NAME "BRANCH NAME",
      SUBSTR(AST.AST_TERMINAL_ID, -4) "TERMINAL",
      AST.AST_ALO_LOCATION_ID "LOCATION",
      CASE WHEN AST.AST_TERMINAL_TYPE =''ATM'' THEN TXN.TRL_ID END AS "ATM COUNT",
	  CASE WHEN AST.AST_TERMINAL_TYPE = ''ATM'' THEN TXN.TRL_AMT_TXN ELSE 0 END AS "ATM TOTAL",
      CASE WHEN AST.AST_TERMINAL_TYPE =''CDM'' THEN TXN.TRL_ID END AS "CAM COUNT",
	  CASE WHEN AST.AST_TERMINAL_TYPE = ''CDM'' THEN TXN.TRL_AMT_TXN ELSE 0 END AS "CDM TOTAL",
CASE WHEN AST.AST_TERMINAL_TYPE =''BRM'' THEN TXN.TRL_ID END AS "BRM COUNT",
CASE WHEN AST.AST_TERMINAL_TYPE = ''BRM'' THEN TXN.TRL_AMT_TXN ELSE 0 END AS "BRM TOTAL"
FROM
      TRANSACTION_LOG TXN
JOIN TRANSACTION_LOG_CUSTOM TXNC ON TXN.TRL_ID = TXNC.TRL_ID
	  Left join cbc_bin cbi on CBI.CBI_ID = (SELECT MIN(CBI_ID) FROM CBC_BIN WHERE CBI_BIN = TXNC.TRL_CARD_BIN) 
      Left join cbc_bank cba on CBI.CBI_CBA_ID = CBA.CBA_ID
      JOIN ATM_STATIONS AST ON TXN.TRL_CARD_ACPT_TERMINAL_IDENT = AST.AST_TERMINAL_ID
      JOIN ATM_BRANCHES ABR ON AST.AST_ABR_ID = ABR.ABR_ID
WHERE
      TXN.TRL_TSC_CODE = 146
      AND TXN.TRL_TQU_ID = ''F''
AND NVL(TXN.TRL_POST_COMPLETION_CODE, '' '') != ''R''
      AND TXN.TRL_ACTION_RESPONSE_CODE = 0
AND ((cba.cba_mnem = {V_Iss_Name}
	  AND (TXN.TRL_DEO_NAME = {V_IE_Deo_Name} OR LPAD(TXN.TRL_ACQR_INST_ID, 10, ''0'') = {V_IE_Acqr_Inst_Id}))
	  OR (TXN.TRL_DEO_NAME = {V_Deo_Name} OR LPAD(TXN.TRL_ACQR_INST_ID, 10, ''0'') = {V_Acqr_Inst_Id}))
      AND AST.AST_TERMINAL_TYPE IN (''ATM'', ''CDM'',''BRM'')
      AND {Branch_Code}
      AND {Terminal}
      AND {Txn_Date}
)
GROUP BY
      "BRANCH CODE",
      "BRANCH NAME",
      "TERMINAL",
      "LOCATION"
)
GROUP BY
      "BRANCH CODE",
      "BRANCH NAME",
      "TERMINAL",
      "LOCATION",
      "ATM COUNT",
      "ATM TOTAL",
      "CAM COUNT",
      "CAM TOTAL",
"BRM COUNT",
"BRM TOTAL"
ORDER BY
      "BRANCH CODE" ASC,
      "TERMINAL" ASC');

