	i_BODY_FIELDS_CBS := TO_CLOB('[{"sequence":1,"sectionName":"1","fieldName":"MERCHANT NAME","csvTxtLength":"18","pdfLength":"18","fieldType":"String","delimiter":";","defaultValue":"MERCHANT NAME","bodyHeader":true,"firstField":true,"leftJustified":true,"padFieldLength":0,"decrypt":false,"decryptionKey":null},{"sequence":2,"sectionName":"2","fieldName":"TRAN","csvTxtLength":"9","pdfLength":"9","fieldType":"String","defaultValue":"TRAN","firstField":false,"bodyHeader":true,"delimiter":";","leftJustified":true,"padFieldLength":0,"decrypt":false,"decryptionKey":null},{"sequence":3,"sectionName":"3","fieldName":"TRAN","csvTxtLength":"15","pdfLength":"15","fieldType":"String","defaultValue":"TRAN","bodyHeader":true,"delimiter":";","leftJustified":true,"padFieldLength":0,"decrypt":false,"decryptionKey":null},{"sequence":4,"sectionName":"4","csvTxtLength":"10","pdfLength":"10","fieldType":"String","delimiter":";","bodyHeader":true,"fieldName":"SETTLEMENT","defaultValue":"SETTLEMENT","leftJustified":true,"padFieldLength":0,"decrypt":false,"decryptionKey":null},{"sequence":5,"sectionName":"5","fieldName":"COMMISSION","csvTxtLength":"13","pdfLength":"13","fieldType":"String","defaultValue":"COMMISSION","bodyHeader":true,"delimiter":";","leftJustified":true,"padFieldLength":0,"decrypt":false,"decryptionKey":null},{"sequence":6,"sectionName":"6","fieldName":"COMMISSION","csvTxtLength":"22","pdfLength":"22","fieldType":"String","defaultValue":"COMMISSION","bodyHeader":true,"fieldFormat":"","delimiter":";","leftJustified":true,"padFieldLength":0,"decrypt":false,"decryptionKey":null},{"sequence":7,"sectionName":"7","fieldName":"NET SETTLEMENT","csvTxtLength":"26","pdfLength":"26","fieldType":"String","defaultValue":"NET SETTLEMENT","bodyHeader":true,"delimiter":";","eol":true,"leftJustified":true,"padFieldLength":0,"decrypt":false,"decryptionKey":null},{"sequence":8,"sectionName":"8","fieldName":"","csvTxtLength":"18","pdfLength":"18","fieldType":"String","defaultValue":"","firstF